/*Table structure for table `alchemy_equipment` */

DROP TABLE IF EXISTS `alchemy_equipment`;

CREATE TABLE `alchemy_equipment` (
  `cid` int(10) unsigned NOT NULL COMMENT '装备cid',
  `type` tinyint(4) unsigned NOT NULL COMMENT '装备type,61:武器,62:防具,63:其他、手套、护盾,64:饰品',
  `level` int(10) unsigned NOT NULL default '1' COMMENT '需要等级',
  `jobs` varchar(200) NOT NULL COMMENT '可装备职业列表,''1,2,3''',
  `pa` int(10) NOT NULL default '0' COMMENT 'Physical Attack 物攻',
  `pd` int(10) NOT NULL default '0' COMMENT 'Physical Defense 物防',
  `ma` int(10) NOT NULL default '0' COMMENT 'Magic Attack 魔攻',
  `md` int(10) NOT NULL default '0' COMMENT 'Magic Defense 魔防',
  `speed` int(10) NOT NULL default '0' COMMENT '速度',
  `hp` int(10) NOT NULL default '0' COMMENT 'hp加成',
  `mp` int(10) NOT NULL default '0' COMMENT 'mp加成',
  `cri` int(10) NOT NULL default '0' COMMENT 'critical 暴击率',
  `dod` int(10) NOT NULL default '0' COMMENT 'Dodge 闪躲率',
  `name` varchar(200) default NULL COMMENT '名称',
  `content` varchar(200) default NULL COMMENT '介绍',
  `class_name` varchar(200) default NULL COMMENT '素材名',
  `is_new` tinyint(4) default '1' COMMENT '是否为新品，1:新，0:非新',
  `can_buy` tinyint(4) default '1' COMMENT '商店内是否可以买到，1:可以，2:不可以',
  `buy_coin` int(10) default NULL COMMENT '商店购买价格，金币',
  `buy_gem` int(10) default NULL COMMENT '商店购买价格，宝石',
  `sale_coin` int(10) default NULL COMMENT '售系统价格,金币',
  `worth` int(10) default NULL COMMENT '售卖顾客价格，金币',
  `durability` decimal(10,0) NOT NULL default '0' COMMENT '耐久度,0为不消耗耐久',
  `can_repair` tinyint(4) default '1' COMMENT '是否可以修理：1,可修;0:不可修',
  `repair_level` int(10) default '1' COMMENT '修理等级:对应需要铁匠铺的等级',
  PRIMARY KEY  (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

