/*Table structure for table `alchemy_order` */

DROP TABLE IF EXISTS `alchemy_order`;

CREATE TABLE `alchemy_order` (
  `cid` int(10) unsigned NOT NULL COMMENT '订单cid',
  `needs` varchar(200) NOT NULL COMMENT '订单需求物品，[[cid,num],[131,2]]',
  `out_time` int(10) unsigned NOT NULL COMMENT '订单限制时间',
  `need_level` int(10) unsigned NOT NULL COMMENT '出现等级，需求用户等级',
  `coin` int(10) unsigned NOT NULL COMMENT '奖励金币数',
  `awards` varchar(200) NOT NULL COMMENT '奖励物品，[[cid,num],[131,2]]',
