  `award_type` tinyint(2) unsigned NOT NULL COMMENT '订单奖励类型（橙色）,1,2,34',
  `avatar_ids` varchar(200) NOT NULL COMMENT 'NPC id,对应avatar_id',
  `avatar_name` varchar(200) NOT NULL COMMENT 'NPC名字,1,2,3,4,',
  `need_iid` varchar(200) NOT NULL COMMENT '对应需求图鉴id',
  `start_dialog_1` varchar(500) NOT NULL COMMENT '订单提需求时对白-1',
  `finish_dialog_1` varchar(500) NOT NULL COMMENT '订单完成时对白-1',
  `drop_dialog_1` varchar(500) NOT NULL COMMENT '订单失败时对白-1',
  `start_dialog_2` varchar(500) NOT NULL,
  `finish_dialog_2` varchar(500) NOT NULL,
  `drop_dialog_2` varchar(500) NOT NULL,
  `start_dialog_3` varchar(500) NOT NULL,
  `finish_dialog_3` varchar(500) NOT NULL,
  `drop_dialog_3` varchar(500) NOT NULL,
  PRIMARY KEY  (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

