/*Table structure for table `alchemy_order_bak` */

DROP TABLE IF EXISTS `alchemy_order_bak`;

CREATE TABLE `alchemy_order_bak` (
  `id` int(10) NOT NULL,
  `needs` varchar(200) NOT NULL COMMENT '订单需求,[[cid,num],[131,1]]',
  `satisfaction` int(10) unsigned NOT NULL default '0' COMMENT '提升满意度',
  `out_time` int(10) unsigned NOT NULL default '0' COMMENT '要出去的时间, 一个固定值',
  `remaining_time` int(10) unsigned NOT NULL default '0' COMMENT '订单剩余时间,-1:无限制',
  `stat` tinyint(3) unsigned NOT NULL default '0' COMMENT '订单状态,1:未接,2:已接,未完成,3:已完成,4:已过期',
  `awards` varchar(200) NOT NULL COMMENT '订单奖励,[]',
  `avatar_id` int(10) unsigned NOT NULL default '0',
  `dialog` varchar(200) NOT NULL default '0' COMMENT '对白',
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

