CREATE TABLE `alchemy_scroll` (
  `cid` int(10) NOT NULL COMMENT '卷轴cid',
  `mix_cid` int(10) default '0' COMMENT '对应合成术cid,技能卷轴则为0',
  `jobs` varchar(200) default NULL COMMENT '可装备的职业列表,''1,2,3''',
  `type` tinyint(4) default NULL COMMENT '卷轴类型,21:合成术,22:技能',
