insert  into `alchemy_story_dialog`(`id`,`nid`,`scene_id`,`dialog`,`user_level`,`fight_level`,`task_id`) values (112,18,101,'[[2,\"你好年轻人，很久没有人来这个地方了。\"],[1,\"老伯，这是什么地方，你怎么会在这里。\"],[2,\"这个矿洞名字叫做矮人矿洞，很多年前，勤劳的矮人们，在这里开采矿石。\"],[1,\"那些矮人呢？\"],[2,\"走啦，去了遥远的大路，隔着大海。留下了这片废弃的矿洞。\"],[1,\"他们为什么要走。\"],[2,\"因为战争，卡尔萨斯王和墨堡国王之间，连年征战。\"],[2,\"啊哈，那都是很久以前的事了，不提也罢。\"],[1,\"老伯，既然矿洞废弃那么久了，那你为什么还留在这里？\"],[2,\"老了，先留在这里，守住过去美好的回忆。\"],[2,\"年轻人，我的腿脚不方便，你能帮我一个忙么？\"],[1,\"当然老伯，有奖励伐？\"],[2,\"哈哈哈，现在的年轻人很现实哟，当然有。不过保密。\"],[1,\"心想：诶，还保密，不会他用过不要的东西吧。\"],[2,\"最近，矿洞通道中，经常有怪声传出，你帮我去查看一下吧。\"],[1,\"没问题，包在我身上。\"]]',1,1,3421),(122,11,2,'[[2,\"我也不是希望发生战争。但是，一直这样和平的话，剑的技术会越来越退步的。\"],[2,\"像我这样优秀的士兵，士官长居然让我去消灭史莱姆这种低级生物。\"],[1,\"这位大叔，你在嘀嘀咕咕在说什么呀？\"],[2,\"哎！矮人矿洞的史莱姆都泛滥成灾了，最讨厌这种黏糊糊的东西了。\"],[2,\"咦？你是谁？走开走开，不要妨碍本少士官站岗\"],[2,\"在战斗中，如果发现打不过对方，我就会选择逃跑，反正逃跑不花钱，而且效果非常好。\"],[2,\"如果你决定出村冒险，点击屏幕右下方冒险按钮。\"]]',1,1,0),(212,18,101,'[[1,\"老伯，我们来拿奖励了，我们找到怪声的源头了。\"],[1,\"事情经过是这~~样地！@#￥！#%！@￥此处省去1万字。\"],[2,\"哈哈哈，年轻人别以为我年纪大了好骗\"],[2,\"我在这里守了几十年了，从来没有见过什么强盗和妖怪出入。\"],[1,\"诶。所以说，好人好事做不得，尤其帮助那些老爷爷啊，老奶奶什么的。\"],[2,\"嗯~要我相信你们也可以，除非你们找到证据。\"],[1,\"哼，让我去把那些坏家伙抓来，看你还有什么话说。\"]]',1,1,5561),(222,11,2,'[[2,\"好久，不打仗了，看我这把武器都已经生锈了。\"],[1,\"你好又见面了。\"],[2,\"嗯？又是你，我已经注意你了，如果有不轨行为，我会立刻把你抓起来\"],[1,\"什么呀\"]]',2,1,0),(312,18,101,'[[2,\"什么这封信是…原来如此，这封信中似乎隐藏着一个大秘密啊。\"],[2,\"年轻人，这件事情事关重大，你必须找到一个代号为“X”的人。\"],[2,\"和我身边的千织婆婆谈一谈，她之前好像提到过这个人。\"],[2,\"对了，这个是说好的奖励。\"],[1,\"这是？\"],[2,\"这件东西叫做魔法铁毡，可以打造出各种兵器装备。我老了，留着没用了。\"],[2,\"还有这些配方，你也拿去吧。\"],[1,\"魔法铁毡“可以用来制造装备”，酷。\"]]',1,1,3541),(322,15,2,'[[2,\"这里是王城小镇，是距离卡尔萨斯城最近的小镇。\"],[2,\"经常有很多冒险者来这里喝酒住店，诶！如果谁在这里开一家道具店的话，肯定赚翻了。\"],[1,\"大婶，你说的太对了，如果想买道具，就来我的店吧\"],[2,\"咦~这么小的孩子开了一家道具店，这个笑话真好笑。\"],[1,\"...\"]]',1,1,0),(412,23,101,'[[1,\"你好，老婆婆，我正在找一个代号叫“X”的人。旁边的老爷爷说你可能知道。\"],[2,\"“砖头”，“麻绳”，“面粉”\"],[1,\"啊？什么呀。\"],[2,\"“砖头”，“麻绳”，“面粉”。\"],[1,\"咦~这个老太婆一定是个机器人，只会说这些。\"],[2,\"年轻人，你能帮我找来这3样东西么。“砖头”，“麻绳”，“面粉”。\"],[1,\"嗯，配方中似乎记载着这些东西的制作方法。\"]]',1,1,0),(422,12,2,'[[2,\"物品和道具必须通过制作与合成后，才会有人愿意买，如果是原材料的话，那肯定是不值钱的\"],[2,\"诶，如果我有一个炼金炉的话，我就能制造出各种物品，嘻嘻\"],[1,\"您真内行，能不能教教我合成方面的知识\"],[2,\"当然，你知道炼金炉么，这个宝贝可以将物质相互转化\"],[2,\"当获得更多的物质和材料后，就可以用他们制造出各种物品\"],[1,\"哇哦，原来是这样。\"],[2,\"当然你还必须拥有相应的制作台和配方，比如工作台可以制造道具，而铁毡可以打造装备\"],[2,\"啊~我说的太多了，去去，我还忙着呢。\"]]',1,1,0),(512,23,101,'[[1,\"老婆婆，快看我带来了砖头麻绳和面粉。\"],[2,\"啊，真不敢相信，你居然找到了。\"],[1,\"哇~机器人终于开口讲话了。\"],[2,\"请你去矿洞门口等我一下\"],[1,\"诶？\"]]',1,1,0),(522,17,2,'[[2,\"嘿~你有没有听过巨龙“瓦格鲁姆”的故事\"],[1,\"咦，小姑娘，你愿意免费跟我分享这个故事么。\"],[2,\"哼，想免费听故事，就找你妈妈去。呸\"],[1,\"哇~现在的孩子精的跟猴似的。\"],[2,\"哼~\"]]',1,1,0),(612,23,101,'[[2,\"艾斯~你们找到艾斯了么？\"],[1,\"婆婆这张照片，是艾斯托我们交给您的。\"],[2,\"这是…20年前艾斯跟我的合影啊。\"],[2,\"艾斯~艾斯他人呢，你们没有把他带回来么\"],[1,\"啊~婆婆是这样的，艾斯加入了卡尔萨斯骑士团了，他答应以后再也不做坏事了。\"],[2,\"是吗？那真是太好了，孩子真是谢谢你了。\"],[1,\"心想：仁慈的主啊，原来我扯谎了，阿弥陀佛，也许这样才是最好的结局。\"],[1,\"千织婆婆，我们要再次踏上冒险之路了。\"],[2,\"要走了么，等等孩子我有件好东西要送给你。\"]]',1,1,0);

