/*Table structure for table `alchemy_effect` */

DROP TABLE IF EXISTS `alchemy_effect`;

CREATE TABLE `alchemy_effect` (
  `cid` int(10) unsigned NOT NULL,
  `name` varchar(32) NOT NULL,
  `content` varchar(200) NOT NULL COMMENT '介绍',
  `class_name` varchar(50) NOT NULL,
  `jobs` varchar(50) NOT NULL DEFAULT '1' COMMENT '1-Warrior 2-Rogue 3-Magus 1,2,3',
  `target` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '目标对象 1-敌方 2-我方',
  `range` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否近身技能 1-近身 2-远程',
  `area` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '攻击范围 1-单体 2-直线行 3-直线列 4-十字 5-全体',
  `dodge_accept` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '闪避率修正',
  `crit_accept` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '暴击率修正',
  `resist` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `mp` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '消耗mp',
  `effect` text,
  `disp_script` text COMMENT '动画表现脚本',
  `ai_script` text,
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

