  `cid` int(10) NOT NULL COMMENT '物品cid',
  `type` tinyint(4) DEFAULT NULL COMMENT '物品type,11:药剂,12:食品,13:工具,14:战斗道具,15:商品',
  `func_id` int(10) DEFAULT NULL COMMENT '物品作用cid',
  `name` varchar(200) DEFAULT NULL COMMENT '名称',
  `tips` varchar(200) DEFAULT NULL COMMENT '简介',
  `content` varchar(200) DEFAULT NULL COMMENT '详细介绍',
  `class_name` varchar(200) DEFAULT NULL COMMENT '素材名',
  `is_new` tinyint(4) DEFAULT '1' COMMENT '是否为新品，1:新，0:非新',
  `can_buy` tinyint(4) DEFAULT '1' COMMENT '商店内是否可以买到，1:可以，2:不可以',
  `buy_coin` int(10) DEFAULT NULL COMMENT '商店购买价格，金币',
  `buy_gem` int(10) DEFAULT NULL COMMENT '商店购买价格，宝石',
  `sale_coin` int(10) DEFAULT NULL COMMENT '售系统价格,金币',
  `worth` int(10) DEFAULT NULL COMMENT '售卖顾客价格，金币',
  `lose` tinyint(3) DEFAULT '0' COMMENT '是否消耗，1-消耗，0-不消耗',
  `need_level` int(10) DEFAULT '0' COMMENT '使用物品需要等级',
  `label` varchar(200) DEFAULT NULL COMMENT '功能类别',
  `buy_level` int(10) DEFAULT '0' COMMENT '购买需要经营等级',
  `sort` int(10) DEFAULT '0' COMMENT '排序字段',
  `useLevel` int(10) DEFAULT '0' COMMENT '使用等级',
  `isGift` smallint(4) DEFAULT '0' COMMENT '是否为礼包',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

