DROP TABLE IF EXISTS `alchemy_init_role`;

CREATE TABLE `alchemy_init_role` (
  `id` int(10) unsigned NOT NULL,
  `rp` int(10) unsigned NOT NULL,
  `gid` int(10) unsigned NOT NULL DEFAULT '1',
  `job` tinyint(4) unsigned NOT NULL DEFAULT '1' COMMENT '1-战士 2-弓手 3-法师',
  `sex` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '名字等基础信息,mercenary_name表，0 为随机',
  `avatar` int(10) unsigned NOT NULL,
