  `face_class_name` varchar(200) NOT NULL,
  `s_face_class_name` varchar(200) NOT NULL,
  `scene_player_class` varchar(200) NOT NULL,
  `skill` varchar(200) NOT NULL COMMENT '技能，[11,0,-1,-1]  0为未装备  -1为未解锁',
  `hp` int(10) unsigned NOT NULL DEFAULT '1',
  `mp` int(10) unsigned NOT NULL DEFAULT '1',
  `phy_att` int(10) unsigned NOT NULL DEFAULT '1',
  `phy_def` int(10) unsigned NOT NULL DEFAULT '1',
  `mag_att` int(10) unsigned NOT NULL DEFAULT '1',
  `mag_def` int(10) unsigned NOT NULL DEFAULT '1',
  `agility` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '速度',
  `crit` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '暴击率 0-1000 (percent)',
  `dodge` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '闪避率 0-1000 (percent)',
  `name` varchar(200) DEFAULT NULL COMMENT '临时英雄用',
  `element` tinyint(3) DEFAULT '1' COMMENT '临时英雄用',
  `weapon` varchar(200) DEFAULT '[]' COMMENT '临时英雄用',
  `fight_corps` int(10) DEFAULT '0' COMMENT '临时英雄用,站位',
  `task_id` int(10) DEFAULT NULL COMMENT '临时英雄用,对应任务id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

