/*Table structure for table `alchemy_monster` */

DROP TABLE IF EXISTS `alchemy_monster`;

CREATE TABLE `alchemy_monster` (
  `cid` int(10) unsigned NOT NULL,
  `tid` int(10) unsigned NOT NULL COMMENT '组别id',
  `job` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '1-Warrior 2-Rogue 3-Magus 1,2,3 ',
  `name` varchar(30) NOT NULL,
  `content` varchar(200) NOT NULL COMMENT '描述',
  `avatar_class_name` varchar(50) NOT NULL COMMENT '素材classname(avatar用)',
  `class_name` varchar(50) NOT NULL COMMENT '素材classname(战斗用)',
  `face_class_name` varchar(50) DEFAULT NULL,
  `s_face_class_name` varchar(50) DEFAULT NULL,
  `element` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '属性：1-风 2-火 3-水',
  `level` int(10) unsigned NOT NULL DEFAULT '1',
  `hp` int(10) unsigned NOT NULL DEFAULT '1',
  `mp` int(10) unsigned NOT NULL DEFAULT '1',
  `phy_att` int(10) unsigned NOT NULL DEFAULT '1',
  `phy_def` int(10) unsigned NOT NULL DEFAULT '1',
  `mag_att` int(10) unsigned NOT NULL DEFAULT '1',
  `mag_def` int(10) unsigned NOT NULL DEFAULT '1',
  `agility` int(10) unsigned NOT NULL DEFAULT '1',
  `crit` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '暴击率 0-100 (percent)',
  `dodge` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '闪避率 0-100 (percent)',
  `size_x` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '长x',
  `size_z` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '宽z',
  `collision_range` tinyint(3) unsigned NOT NULL COMMENT '碰撞范围',
  `is_boss` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否boss',
  `weapon` varchar(200) NOT NULL DEFAULT '[]' COMMENT '[1,2,3,4]',
  `skill` varchar(200) NOT NULL DEFAULT '[]' COMMENT '[1,2,3]',
  `award_conditions` varchar(1000) NOT NULL DEFAULT '[]',
  `first_award_conditions` varchar(1000) NOT NULL DEFAULT '[]',
  `award_exp` int(10) unsigned NOT NULL,
  `talk` varchar(255) NOT NULL DEFAULT '' COMMENT '战斗开始前对话',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

