/*Table structure for table `alchemy_scene` */

DROP TABLE IF EXISTS `alchemy_scene`;

CREATE TABLE `alchemy_scene` (
  `id` int(10) unsigned NOT NULL,
  `type` tinyint(3) NOT NULL COMMENT '类型:1,大地图,2,副本,3,家',
  `name` varchar(30) NOT NULL,
  `content` varchar(255) NOT NULL,
  `bg` varchar(200) NOT NULL DEFAULT 'null' COMMENT '场景背景图类名',
  `bg_sound` varchar(200) NOT NULL DEFAULT 'null' COMMENT '场景特有的背景音乐,一个mp3地址',
  `node_str` varchar(200) NOT NULL DEFAULT 'null' COMMENT '地图行走区域信息',
  `entrances` varchar(200) NOT NULL DEFAULT '[]' COMMENT '场景的入口坐标列表,[[11,23],[88,23]]',
  `num_cols` int(10) NOT NULL COMMENT 'grid行数',
  `num_rows` int(10) NOT NULL COMMENT 'grid列数',
  `iso_star_x` tinyint(3) NOT NULL COMMENT 'iso场景容器坐标X',
  `iso_star_y` tinyint(3) NOT NULL COMMENT 'iso场景容器坐标Y',
  `parent_sceneId` int(10) NOT NULL COMMENT '父场景ID',
  `need_level` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '需要等级',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

