/*Table structure for table `alchemy_scroll` */

DROP TABLE IF EXISTS `alchemy_scroll`;

CREATE TABLE `alchemy_scroll` (
  `cid` int(10) NOT NULL COMMENT '卷轴cid',
  `mix_cid` int(10) DEFAULT '0' COMMENT '对应合成术cid,技能卷轴则为0',
  `jobs` varchar(200) DEFAULT NULL COMMENT '可装备的职业列表,''1,2,3''',
  `type` tinyint(4) DEFAULT NULL COMMENT '卷轴类型,21:合成术,22:技能',
  `element` varchar(200) DEFAULT '1,2,3' COMMENT '卷轴要求属性,1:风,2:火,3:水',
  `name` varchar(200) DEFAULT NULL COMMENT '名称',
  `content` varchar(200) DEFAULT NULL COMMENT '介绍',
  `class_name` varchar(200) DEFAULT NULL COMMENT '卷轴素材名',
  `skill_class_name` varchar(200) DEFAULT NULL COMMENT '技能素材名',
  `need_level` int(10) DEFAULT '0' COMMENT '需要用户等级（战斗等级）',
  `is_new` tinyint(4) DEFAULT '1' COMMENT '是否为新品，1:新，0:非新',
  `can_buy` tinyint(4) DEFAULT '1' COMMENT '商店内是否可以买到，1:可以，2:不可以',
  `buy_coin` int(10) DEFAULT NULL COMMENT '商店购买价格，金币',
  `buy_gem` int(10) DEFAULT NULL COMMENT '商店购买价格，宝石',
  `sale_coin` int(10) DEFAULT NULL COMMENT '售系统价格,金币',
  `worth` int(10) DEFAULT NULL COMMENT '售卖顾客价格，金币',
  `buy_level` int(10) DEFAULT '0' COMMENT '购买需要经营等级',
  `sort` int(10) DEFAULT '0' COMMENT '排序字段',
  `probability` int(10) DEFAULT '0' COMMENT '概率，权重',
  `hire_type` varchar(200) DEFAULT '1,2,3' COMMENT '可以出现在哪个酒馆：1,2,3',
  `hire_level` int(10) DEFAULT '0' COMMENT '需要酒馆等级',
  `rp` int(10) DEFAULT '1' COMMENT '对应佣兵星级',
  `study_level` int(10) DEFAULT '1' COMMENT '传授需要佣兵等级',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

