/*Table structure for table `alchemy_story_action` */

DROP TABLE IF EXISTS `alchemy_story_action`;

CREATE TABLE `alchemy_story_action` (
  `aid` int(10) unsigned NOT NULL COMMENT '动作id，对应 story 表 action_ids',
  `nid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '演员id，对应 story_npc 表 nid',
  `x` int(11) NOT NULL DEFAULT '0' COMMENT '演员要到达的位置的x位置',
  `y` int(11) NOT NULL DEFAULT '0' COMMENT '演员要到达的位置的y坐标',
  `faceX` int(11) NOT NULL DEFAULT '0' COMMENT '演员要面向的位置的x坐标',
  `faceY` int(11) NOT NULL DEFAULT '0' COMMENT '演员要面向的位置的y坐标',
  `content` varchar(200) DEFAULT NULL COMMENT '演员要说的话',
  `chat_time` int(11) NOT NULL COMMENT '对白时间，毫秒单位',
  `camera` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '是否镜头跟随,1:跟随,0：否',
  `wait` tinyint(2) unsigned NOT NULL COMMENT '是否等待这个完成再执行下一步操作，1:是,0:否',
  `immediately` tinyint(2) unsigned NOT NULL COMMENT '演员是否立即出现在指定位置，1:是，0:否',
  `hide` tinyint(2) unsigned NOT NULL COMMENT '是否隐藏演员,1:是,0:否',
  `class_name` varchar(200) DEFAULT NULL COMMENT '要在角色身上叠加的动画',
  `shock_screen_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '振屏时间',
  `action_label` varchar(200) DEFAULT NULL COMMENT '要播放的演员的动作的标签',
  `label_times` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '要让演员播放actionLabel动作的次数',
  `to_stop` tinyint(2) unsigned DEFAULT '0' COMMENT '停留信息',
  PRIMARY KEY (`aid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

