/*Table structure for table `alchemy_story_dialog` */

DROP TABLE IF EXISTS `alchemy_story_dialog`;

CREATE TABLE `alchemy_story_dialog` (
  `id` int(10) NOT NULL,
  `nid` int(10) NOT NULL COMMENT 'npc id',
  `scene_id` int(10) NOT NULL COMMENT '场景Id',
  `dialog` varchar(2000) DEFAULT NULL COMMENT '对白',
  `user_level` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '经营等级',
  `fight_level` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '冒险等级',
  `task_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '触发任务id',
  `fight_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '触发战斗,战斗站位id，对应 alchemy_fight_monster_matrix',
  `fight_type` tinyint(4) unsigned NOT NULL DEFAULT '1' COMMENT '战斗结果需求,1:必须成功,0:可以失败',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

