insert  into `alchemy_story_dialog`(`id`,`nid`,`scene_id`,`dialog`,`user_level`,`fight_level`,`task_id`,`fight_id`,`fight_type`) values (112,18,101,'[[2,\"年轻人，我的腿脚不方便，你能帮我一个忙么？\"],[1,\"当然老伯，有奖励伐？\"],[2,\"哈哈哈，现在的年轻人很现实哟，当然有。不过保密。\"],[2,\"最近，矿洞通道中，黑色史莱姆太泛滥，如果你能帮我清除一些的话...\"],[1,\"没问题，包在我身上。\"],[2,\"很好，这是矿洞通道一层的钥匙。\"],[2,\"我这里还有些老旧的装备，记得要装备哦。\"],[1,\"这，怎么装备啊？\"]]',1,1,0,0,0),(122,11,2,'[[2,\"我也不是希望发生战争。但是，一直这样和平的话，剑的技术会越来越退步的。\"],[2,\"像我这样优秀的士兵，士官长居然让我去消灭史莱姆这种低级生物。\"],[1,\"这位大叔，你在嘀嘀咕咕在说什么呀？\"],[2,\"哎！矮人矿洞的史莱姆都泛滥成灾了，最讨厌这种黏糊糊的东西了。\"],[2,\"咦？你是谁？走开走开，不要妨碍本少士官站岗\"],[2,\"在战斗中，如果发现打不过对方，我就会选择逃跑，反正逃跑不花钱，而且效果非常好。\"],[2,\"如果你决定出村冒险，点击屏幕右下方冒险按钮。\"]]',1,1,0,0,0),(132,81,2,'[[1,\"HI，肉肉……额？\"]]',1,1,0,0,0),(152,115,201,'[[2,\"真的不见了，该怎么办呢？\"]]',1,1,0,0,0),(212,18,101,'[[2,\"什么，你是说你打败了大三角石头怪？\"],[1,\"是啊，我很轻松的就ko了它\"],[1,\"不仅如此，我还发现有盗贼混进了矿洞。\"],[2,\"什么？，这帮家伙到底想干什么。\"],[2,\"拿着钥匙去矿洞5层，看看他们到底要干嘛。\"]]',1,1,0,0,0),(222,11,2,'[[2,\"好久，不打仗了，看我这把武器都已经生锈了。\"],[1,\"你好又见面了。\"],[2,\"嗯？又是你，我已经注意你了，如果有不轨行为，我会立刻把你抓起来\"],[1,\"什么呀\"]]',2,1,0,0,0),(232,81,2,'[[1,\"嘿！肉肉，给你….\"]]',1,1,0,0,0),(252,115,201,'[[1,\"给，这是你的情书。\"],[2,\"真是太感谢你们了。\"],[1,\"小事一桩，如果能给点额外报酬就最好了啦。\"],[2,\"那我就把这些道具也给你们吧。\"],[1,\"接下来，我也要早点鼓起勇气把这份信送出去。\"]]',1,1,0,0,0),(312,18,101,'[[2,\"什么这封信是…原来如此，这封信中似乎隐藏着一个大秘密啊。\"],[2,\"年轻人，这件事情事关重大，你必须找到一个叫无面者的人。\"],[2,\"和我身边的千织婆婆谈一谈，她之前好像提到过这个人。\"],[2,\"对了，这个是说好的奖励。\"],[1,\"这是？\"],[2,\"这件东西叫做魔法铁毡，可以打造出各种兵器装备。我老了，留着没用了。\"],[2,\"还有这些配方，你也拿去吧。\"],[1,\"魔法铁毡“可以用来制造装备”，酷。\"]]',1,1,0,0,0),(322,15,2,'[[2,\"这里是王城小镇，是距离卡尔萨斯城最近的小镇。\"],[2,\"经常有很多冒险者来这里喝酒住店，诶！如果谁在这里开一家道具店的话，肯定赚翻了。\"],[1,\"大婶，你说的太对了，如果想买道具，就来我的店吧\"],[2,\"咦~这么小的孩子开了一家道具店，这个笑话真好笑。\"],[1,\"...\"]]',1,1,0,0,0),(332,82,113,'[[2,\"啊！救命啊….\"],[1,\"可乐你看,快救那个人！.\"]]',1,1,0,47,1),(352,120,2,'[[3,\"请问，你们能帮我一个忙吗？\"]]',1,1,0,0,0),(412,23,101,'[[1,\"你好，老婆婆，我正在找一个叫无面者的人。旁边的老爷爷说你可能知道。\"],[2,\"“面粉”\"],[1,\"啊？什么呀。\"],[2,\"“面粉”。\"],[1,\"咦~这个老太婆一定是个机器人，只会说这些。\"],[2,\"年轻人，你能帮我找到“面粉”么。\"],[1,\"嗯？我怎样才能找到面粉？\"],[2,\"去找酒店老板娘梅丽，她应该知道面粉的制作方法。\"]]',1,1,0,0,0),(422,12,2,'[[2,\"物品和道具必须通过制作与合成后，才会有人愿意买，如果是原材料的话，那肯定是不值钱的\"],[2,\"诶，如果我有一个炼金炉的话，我就能制造出各种物品，嘻嘻\"],[1,\"您真内行，能不能教教我合成方面的知识\"],[2,\"当然，你知道炼金炉么，这个宝贝可以将物质相互转化\"],[2,\"当获得更多的物质和材料后，就可以用他们制造出各种物品\"],[1,\"哇哦，原来是这样。\"],[2,\"当然你还必须拥有相应的制作台和配方，比如工作台可以制造道具，而铁毡可以打造装备\"],[2,\"啊~我说的太多了，去去，我还忙着呢。\"]]',1,1,0,0,0),(432,82,113,'[[1,\"来了来了，这里是3个石头，给你.\"]]',1,1,0,0,0),(452,121,302,'[[2,\"最近怪物好多啊。\"]]',1,1,0,0,0),(512,23,101,'[[1,\"老婆婆，快看我带来了砖头麻绳和面粉。\"],[2,\"啊，真不敢相信，你居然找到了。\"],[1,\"哇~机器人终于开口讲话了。\"],[2,\"请你去矿洞门口等我一下\"],[1,\"诶？\"]]',1,1,0,0,0),(522,17,2,'[[2,\"嘿~你有没有听过巨龙“瓦格鲁姆”的故事\"],[1,\"咦，小姑娘，你愿意免费跟我分享这个故事么。\"],[2,\"哼，想免费听故事，就找你妈妈去。呸\"],[1,\"哇~现在的孩子精的跟猴似的。\"],[2,\"哼~\"]]',1,1,0,0,0),(532,84,101,'[[1,\"似乎发生了什么事情？？.\"]]',1,1,0,0,0),(552,121,302,'[[2,\"真是了不起，你们竟然打倒了那么多怪物。\"],[1,\"小菜一碟。\"],[2,\"这样等这里的采集工作结束，我也能回去了。\"],[2,\"啊，对了，这个给你们，作为给你们的答谢。\"]]',1,1,0,0,0),(612,23,101,'[[2,\"艾斯~你们找到艾斯了么？\"],[1,\"婆婆这张照片，是艾斯托我们交给您的。\"],[2,\"这是…艾斯小的时候跟我的合影啊。\"],[2,\"艾斯~艾斯他人呢，你们没有把他带回来么\"],[1,\"啊~婆婆是这样的，艾斯加入了卡尔萨斯骑士团了，他答应以后再也不做坏事了。\"],[2,\"是吗？那真是太好了，孩子真是谢谢你了。\"],[1,\"心想：仁慈的主啊，阿弥陀佛，原谅我扯谎了，但也许这样才是最好的结局。\"],[1,\"千织婆婆，我们要再次踏上冒险之路了。\"],[2,\"等等孩子，我有件好东西要送给你。\"],[2,\"这个魔法缝纫机，可以用来制作各种布衣和皮具\"]]',1,1,0,0,0),(622,107,101,'[[2,\"你在找老洛克么，他去矿洞深处3层采矿了。\"]]',1,1,0,0,0),(632,86,109,'[[2,\"你们终于来了.\"]]',1,1,0,0,0),(652,120,2,'[[2,\"爸爸被怪物攻击了，有没有受伤？\"],[1,\"放心，他没事，我这次买一送一顺带完成了父亲给的任务，所以那个烧肉能多给我点吗？\"],[2,\"当然可以。\"],[1,\"另外，你父亲让我告诉你说，午餐很好吃，你长大了。\"],[2,\"是吗，爸爸高兴就好了。\"],[2,\"这些道具也给你们，谢谢你们的帮助。\"],]',1,1,0,0,0),(712,18,101,'[[2,\"路上小心，年轻人。\"],[2,\"哦对了，我给你的那个铁毡，要把他放置在房屋中才能够使用哦。\"],[1,\"嗯记住了，回家后点击放置按钮。\"]]',1,1,0,0,0),(722,112,201,'[[2,\"你好冒险者，竞技场正在筹办中。\"]]',1,1,0,0,0),(732,87,108,'[[2,\"看这岩石，似乎....\"]]',1,1,0,0,0),(752,122,301,'[[2,\"我到底该怎么说，才能让她明白呢？\"]]',1,1,0,0,0),(812,23,101,'[[2,\"太好了，我好开心啊，艾斯他是个好孩子。\"]]',1,1,0,0,0),(822,114,201,'[[2,\"一二，一二，这里是训练营\"],[1,\"哇~我要训练，有没有年卡。\"],[2,\"你好，冒险者，训练营正在建设中。\"]]',1,1,0,0,0),(832,84,101,'[[1,\"夏利，怎么样了他们....\"]]',1,1,0,0,0),(852,122,301,'[[2,\"原来如此，不过，我不知道该送什么？\"],[1,\"那要看你要送谁，对象是猫就送条鱼，对象是狗就送骨头。\"],[2,\"这样啊，那我想送花给