/*Table structure for table `alchemy_task` */

DROP TABLE IF EXISTS `alchemy_task`;

CREATE TABLE `alchemy_task` (
  `id` int(10) unsigned NOT NULL,
  `label` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '任务标签-1:主线 2:分支 3:日常',
  `priority` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '优先级',
  `condition_ids` varchar(255) NOT NULL DEFAULT '[]' COMMENT '条件ids',
  `complete_cost` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '任务符数量 0:不可用任务符直接完成',
  `need_user_level` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '需要经营等级',
  `need_fight_level` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '需要战斗等级',
  `front_task_id` varchar(100) NOT NULL DEFAULT '[]' COMMENT '前置任务',
  `next_task_id` varchar(100) NOT NULL DEFAULT '[]' COMMENT '后续任务',
  `from_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '任务来源 0:无 1:npc对话 2:npc发布 3:剧情 4:自动接受任务（任务后接任务）',
  `is_auto_complete` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否自动完成',
  `title` varchar(64) DEFAULT NULL COMMENT '任务标题',
  `npc_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'npcid',
  `npc_name` varchar(20) NOT NULL DEFAULT '' COMMENT 'npcname',
  `npc_classname` varchar(50) NOT NULL DEFAULT '' COMMENT 'npc头像',
  `worldmap_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所在世界地图cid',
  `foreword` varchar(255) DEFAULT NULL COMMENT 'npc讲话',
  `help_desp` varchar(255) DEFAULT NULL COMMENT '详细描述',
  `done_desp` varchar(255) DEFAULT NULL COMMENT '完成对话',
  `guide` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '引导',
  `accept_story` varchar(200) NOT NULL COMMENT '接受后触发 剧情/对话,不允许同时出现多个剧情',
  `story` varchar(200) NOT NULL COMMENT '完成后触发 剧情/对话,不允许同时出现多个剧情',
  `awards` varchar(1000) NOT NULL DEFAULT '[]' COMMENT '任务奖励',
  PRIMARY KEY (`id`)
